import "./App.css";
import React, { useState } from "react";
import Details from "./details";

function App() {
  const [message, setMessage] = useState("Lutfen giris yapiniz");
  const [username, setUsername] = useState("");
  const [surname, setSurname] = useState("");
  const [password, setPassword] = useState("");

  function loginFunction() {
    if (username.search(/[^a-zA-Z]+/)) {
      setMessage("Hosgeldin " + username + " " + surname);
    }else{
      alert ("User ")
    }

  }
  return (
    <>
      <Details name={message} />
      <div className="wrapper">
        <input
          type="text"
          className="form-input"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Kullanici Adini Giriniz"
        />
        <input
          type="text"
          className="form-input"
          value={surname}
          onChange={(e) => setSurname(e.target.value)}
          placeholder="Soyadinizi Giriniz"
        />
        <input
          type="password"
          className="form-input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Sifrenizi Giriniz"
        />
        <button onClick={loginFunction}>Giris yap</button>
      </div>
    </>
  );
}

export default App;
